    @Cache(usage = NONSTRICT_READ_WRITE)
    @JoinColumn(name = "product_shipment_id")
    @ManyToOne(cascade = PERSIST, fetch = LAZY)
    public TdProductShipment getProductShipment() {
        return productShipment;
    }

    /**
     * Set the productShipment without adding this TdProduct instance on the passed ProductShipment
     * If you want to preserve referential integrity we recommend to use
     * instead the corresponding adder method provided by {@link TdProductShipment}
     */
    public void setProductShipment(TdProductShipment productShipment) {
        this.productShipment = productShipment;

        // We set the foreign key property so it can be used by our JPA search by Example facility.
        if (productShipment != null) {
            setProductShipmentId(productShipment.getProductShipmentId());
        } else {
            setProductShipmentId(null);
        }
    }
    
    
