package com.app.mpadmin.web.converter.domain;

import javax.inject.Inject;
import org.springframework.stereotype.Component;
import com.app.mpadmin.domain.TdCity;
import com.app.mpadmin.repository.TdCityRepository;
import com.app.mpadmin.web.converter.GenericJsfConverter;

/**
 * Responsible for creating TdCity JSF converters.
 * Each converter provides a 'print' method to convert an entity instance to a friendly string representation (readable by humans...).
 */
@Component
public class TdCityConverter extends GenericJsfConverter<TdCity, Integer> {
    @Inject
    public TdCityConverter(TdCityRepository tdCityRepository) {
        super(tdCityRepository, Integer.class);
    }

    @Override
    public String print(TdCity tdCity) {
        return tdCity == null ? "" : "" + tdCity.getCityName() + "/" + tdCity.getCityName2() + "/" + tdCity.getIsBigcity();
    }
}