package com.app.mpadmin.web.converter.domain;

import javax.inject.Inject;
import org.springframework.stereotype.Component;
import com.app.mpadmin.domain.TdPage;
import com.app.mpadmin.repository.TdPageRepository;
import com.app.mpadmin.web.converter.GenericJsfConverter;

/**
 * Responsible for creating TdPage JSF converters.
 * Each converter provides a 'print' method to convert an entity instance to a friendly string representation (readable by humans...).
 */
@Component
public class TdPageConverter extends GenericJsfConverter<TdPage, Integer> {
    @Inject
    public TdPageConverter(TdPageRepository tdPageRepository) {
        super(tdPageRepository, Integer.class);
    }

    @Override
    public String print(TdPage tdPage) {
        return tdPage == null ? "" : "" + tdPage.getPageHeading() + "/" + tdPage.getPageShortDesc() + "/" + tdPage.getPageLongDesc();
    }
}