package com.app.mpadmin.web.converter.domain;

import javax.inject.Inject;
import org.springframework.stereotype.Component;
import com.app.mpadmin.domain.TdState;
import com.app.mpadmin.repository.TdStateRepository;
import com.app.mpadmin.web.converter.GenericJsfConverter;

/**
 * Responsible for creating TdState JSF converters.
 * Each converter provides a 'print' method to convert an entity instance to a friendly string representation (readable by humans...).
 */
@Component
public class TdStateConverter extends GenericJsfConverter<TdState, Integer> {
    @Inject
    public TdStateConverter(TdStateRepository tdStateRepository) {
        super(tdStateRepository, Integer.class);
    }

    @Override
    public String print(TdState tdState) {
        return tdState == null ? "" : "" + tdState.getStateName() + "/" + tdState.getDescShort() + "/" + tdState.getDescLong();
    }
}