package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.AuthGroup;
import com.app.mpadmin.repository.AuthGroupRepository;
import com.app.mpadmin.web.converter.domain.AuthGroupConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class AuthGroupController {
    private static final long serialVersionUID = 1L;

    @Inject
    private AuthGroupRepository authGroupRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private AuthGroupConverter authGroupConverter;

    public AuthGroup newIfNull(AuthGroup authGroup) {
        if (authGroup != null) {
            return authGroup;
        }

        return authGroupRepository.getNewWithDefaults();
    }

    public boolean save(AuthGroup authGroup) {
        authGroupRepository.save(authGroup);
        messageUtil.info("status_saved_ok", authGroupConverter.print(authGroup));
        return true;
    }

    public boolean saveAndClose(AuthGroup authGroup) {
        authGroupRepository.save(authGroup);
        messageUtil.infoDelayed("status_saved_ok", authGroupConverter.print(authGroup));
        forceClose();
        return true;
    }

    public boolean delete(AuthGroup authGroup) {
        String infoArg = authGroupConverter.print(authGroup);
        authGroupRepository.delete(authGroup);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}