package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.AuthUserGroups;
import com.app.mpadmin.repository.AuthUserGroupsRepository;
import com.app.mpadmin.web.converter.domain.AuthUserGroupsConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class AuthUserGroupsController {
    private static final long serialVersionUID = 1L;

    @Inject
    private AuthUserGroupsRepository authUserGroupsRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private AuthUserGroupsConverter authUserGroupsConverter;

    public AuthUserGroups newIfNull(AuthUserGroups authUserGroups) {
        if (authUserGroups != null) {
            return authUserGroups;
        }

        return authUserGroupsRepository.getNewWithDefaults();
    }

    public boolean save(AuthUserGroups authUserGroups) {
        authUserGroupsRepository.save(authUserGroups);
        messageUtil.info("status_saved_ok", authUserGroupsConverter.print(authUserGroups));
        return true;
    }

    public boolean saveAndClose(AuthUserGroups authUserGroups) {
        authUserGroupsRepository.save(authUserGroups);
        messageUtil.infoDelayed("status_saved_ok", authUserGroupsConverter.print(authUserGroups));
        forceClose();
        return true;
    }

    public boolean delete(AuthUserGroups authUserGroups) {
        String infoArg = authUserGroupsConverter.print(authUserGroups);
        authUserGroupsRepository.delete(authUserGroups);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}