package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdChangePassword;
import com.app.mpadmin.repository.TdChangePasswordRepository;
import com.app.mpadmin.web.converter.domain.TdChangePasswordConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdChangePasswordController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdChangePasswordRepository tdChangePasswordRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdChangePasswordConverter tdChangePasswordConverter;

    public TdChangePassword newIfNull(TdChangePassword tdChangePassword) {
        if (tdChangePassword != null) {
            return tdChangePassword;
        }

        return tdChangePasswordRepository.getNewWithDefaults();
    }

    public boolean save(TdChangePassword tdChangePassword) {
        tdChangePasswordRepository.save(tdChangePassword);
        messageUtil.info("status_saved_ok", tdChangePasswordConverter.print(tdChangePassword));
        return true;
    }

    public boolean saveAndClose(TdChangePassword tdChangePassword) {
        tdChangePasswordRepository.save(tdChangePassword);
        messageUtil.infoDelayed("status_saved_ok", tdChangePasswordConverter.print(tdChangePassword));
        forceClose();
        return true;
    }

    public boolean delete(TdChangePassword tdChangePassword) {
        String infoArg = tdChangePasswordConverter.print(tdChangePassword);
        tdChangePassword.setIsActive(new Integer(0));
        tdChangePasswordRepository.save(tdChangePassword);
        //tdChangePasswordRepository.delete(tdChangePassword);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}