package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdCity;
import com.app.mpadmin.repository.TdCityRepository;
import com.app.mpadmin.web.converter.domain.TdCityConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdCityController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdCityRepository tdCityRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdCityConverter tdCityConverter;

    public TdCity newIfNull(TdCity tdCity) {
        if (tdCity != null) {
            return tdCity;
        }

        return tdCityRepository.getNewWithDefaults();
    }

    public boolean save(TdCity tdCity) {
        tdCityRepository.save(tdCity);
        messageUtil.info("status_saved_ok", tdCityConverter.print(tdCity));
        return true;
    }

    public boolean saveAndClose(TdCity tdCity) {
        tdCityRepository.save(tdCity);
        messageUtil.infoDelayed("status_saved_ok", tdCityConverter.print(tdCity));
        forceClose();
        return true;
    }

    public boolean delete(TdCity tdCity) {
        String infoArg = tdCityConverter.print(tdCity);
        tdCityRepository.delete(tdCity);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}