package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdComment;
import com.app.mpadmin.repository.TdCommentRepository;
import com.app.mpadmin.web.converter.domain.TdCommentConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdCommentController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdCommentRepository tdCommentRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdCommentConverter tdCommentConverter;

    public TdComment newIfNull(TdComment tdComment) {
        if (tdComment != null) {
            return tdComment;
        }

        return tdCommentRepository.getNewWithDefaults();
    }

    public boolean save(TdComment tdComment) {
        tdCommentRepository.save(tdComment);
        messageUtil.info("status_saved_ok", tdCommentConverter.print(tdComment));
        return true;
    }

    public boolean saveAndClose(TdComment tdComment) {
        tdCommentRepository.save(tdComment);
        messageUtil.infoDelayed("status_saved_ok", tdCommentConverter.print(tdComment));
        forceClose();
        return true;
    }

    public boolean delete(TdComment tdComment) {
        String infoArg = tdCommentConverter.print(tdComment);
        tdCommentRepository.delete(tdComment);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}