package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdMake;
import com.app.mpadmin.repository.TdMakeRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdMakeLazyDataModel extends GenericLazyDataModel<TdMake> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdMakeRepository tdMakeRepository;
    @Inject
    transient private TdMakeSearchForm tdMakeSearchForm;

    List<TdMake> datasource;

        @Override
        public TdMake getRowData(String rowKey) {
            for(TdMake tdMake : datasource) {
                if(tdMake.getMakeId().equals(rowKey))
                    return tdMake;
            }

            return null;
        }

        @Override
        public Object getRowKey(TdMake tdMake)
        {
             return tdMake.getMakeId();
        }

    /**
     * Prepare the search parameters and call the tdMakeRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdMake> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdMakeSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdMakeSearchForm.getIsActiveRange());
        sp.addRange(tdMakeSearchForm.getStartingYearOpsRange());
        sp.addRange(tdMakeSearchForm.getRepresentCountryIdRange());
        sp.addRange(tdMakeSearchForm.getMakeStatusRange());
        sp.addRange(tdMakeSearchForm.getUpdationDateRange());

        TdMake tdMake = tdMakeSearchForm.getTdMake();
        setRowCount(tdMakeRepository.findCount(tdMake, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data
        datasource = tdMakeRepository.find(tdMake, sp);
        return datasource;
    }
}