package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdMakeModel;
import com.app.mpadmin.repository.TdMakeModelRepository;
import com.app.mpadmin.web.converter.domain.TdMakeModelConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdMakeModelController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdMakeModelRepository tdMakeModelRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdMakeModelConverter tdMakeModelConverter;

    public TdMakeModel newIfNull(TdMakeModel tdMakeModel) {
        if (tdMakeModel != null) {
            return tdMakeModel;
        }

        return tdMakeModelRepository.getNewWithDefaults();
    }

    public boolean save(TdMakeModel tdMakeModel) {
        tdMakeModelRepository.save(tdMakeModel);
        messageUtil.info("status_saved_ok", tdMakeModelConverter.print(tdMakeModel));
        return true;
    }

    public boolean saveAndClose(TdMakeModel tdMakeModel) {
        tdMakeModelRepository.save(tdMakeModel);
        messageUtil.infoDelayed("status_saved_ok", tdMakeModelConverter.print(tdMakeModel));
        forceClose();
        return true;
    }

    public boolean delete(TdMakeModel tdMakeModel) {
        String infoArg = tdMakeModelConverter.print(tdMakeModel);
        tdMakeModelRepository.delete(tdMakeModel);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}