package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdPage;
import com.app.mpadmin.repository.TdPageRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdPageLazyDataModel extends GenericLazyDataModel<TdPage> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdPageRepository tdPageRepository;
    @Inject
    transient private TdPageSearchForm tdPageSearchForm;


    List<TdPage> datasource;

    @Override
    public TdPage getRowData(String rowKey) {
        for(TdPage tdPage : datasource) {
            if(tdPage.getPageId().equals(rowKey))
                return tdPage;
        }

        return null;
    }

    @Override
    public Object getRowKey(TdPage tdPage)
    {
         return tdPage.getPageId();
    }


    /**
     * Prepare the search parameters and call the tdPageRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdPage> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdPageSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdPageSearchForm.getPageTypeRange());
        sp.addRange(tdPageSearchForm.getPageReferenceIdRange());
        sp.addRange(tdPageSearchForm.getPageIsActiveRange());
        sp.addRange(tdPageSearchForm.getPagePriorityRange());
        sp.addRange(tdPageSearchForm.getUpdationDateRange());
        sp.addRange(tdPageSearchForm.getPageUserIdRange());
        sp.addRange(tdPageSearchForm.getPageCustom3Range());
        sp.addRange(tdPageSearchForm.getPageCustom4Range());

        TdPage tdPage = tdPageSearchForm.getTdPage();
        setRowCount(tdPageRepository.findCount(tdPage, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data

        datasource = tdPageRepository.find(tdPage, sp);
        return datasource;
    }
}