package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdProductBrand;
import com.app.mpadmin.repository.TdProductBrandRepository;
import com.app.mpadmin.web.converter.domain.TdProductBrandConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdProductBrandController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdProductBrandRepository tdProductBrandRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdProductBrandConverter tdProductBrandConverter;

    public TdProductBrand newIfNull(TdProductBrand tdProductBrand) {
        if (tdProductBrand != null) {
            return tdProductBrand;
        }

        return tdProductBrandRepository.getNewWithDefaults();
    }

    public boolean save(TdProductBrand tdProductBrand) {
        tdProductBrandRepository.save(tdProductBrand);
        messageUtil.info("status_saved_ok", tdProductBrandConverter.print(tdProductBrand));
        return true;
    }

    public boolean saveAndClose(TdProductBrand tdProductBrand) {
        tdProductBrandRepository.save(tdProductBrand);
        messageUtil.infoDelayed("status_saved_ok", tdProductBrandConverter.print(tdProductBrand));
        forceClose();
        return true;
    }

    public boolean delete(TdProductBrand tdProductBrand) {
        String infoArg = tdProductBrandConverter.print(tdProductBrand);
        tdProductBrandRepository.delete(tdProductBrand);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}