package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdProductBrand;
import com.app.mpadmin.repository.TdProductBrandRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdProductBrandLazyDataModel extends GenericLazyDataModel<TdProductBrand> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdProductBrandRepository tdProductBrandRepository;
    @Inject
    transient private TdProductBrandSearchForm tdProductBrandSearchForm;

    /**
     * Prepare the search parameters and call the tdProductBrandRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdProductBrand> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdProductBrandSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdProductBrandSearchForm.getInternalRatingRange());
        sp.addRange(tdProductBrandSearchForm.getUpdationDateRange());
        sp.addRange(tdProductBrandSearchForm.getIsActiveRange());

        // property selectors
        sp.clearPropertySelectors();
        sp.addPropertySelector(tdProductBrandSearchForm.getBrandStatusSelector());

        TdProductBrand tdProductBrand = tdProductBrandSearchForm.getTdProductBrand();
        setRowCount(tdProductBrandRepository.findCount(tdProductBrand, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data

        return tdProductBrandRepository.find(tdProductBrand, sp);
    }
}