package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdProductModel;
import com.app.mpadmin.repository.TdProductModelRepository;
import com.app.mpadmin.web.converter.domain.TdProductModelConverter;
import com.app.mpadmin.web.util.MessageUtil;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdProductModelController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdProductModelRepository tdProductModelRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdProductModelConverter tdProductModelConverter;

    public TdProductModel newIfNull(TdProductModel tdProductModel) {
        if (tdProductModel != null) {
            return tdProductModel;
        }

        return tdProductModelRepository.getNewWithDefaults();
    }

    public boolean save(TdProductModel tdProductModel) {
        tdProductModelRepository.save(tdProductModel);
        messageUtil.info("status_saved_ok", tdProductModelConverter.print(tdProductModel));
        return true;
    }

    public boolean saveAndClose(TdProductModel tdProductModel) {
        tdProductModelRepository.save(tdProductModel);
        messageUtil.infoDelayed("status_saved_ok", tdProductModelConverter.print(tdProductModel));
        forceClose();
        return true;
    }

    public boolean delete(TdProductModel tdProductModel) {
        String infoArg = tdProductModelConverter.print(tdProductModel);
        tdProductModelRepository.delete(tdProductModel);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }

    public boolean deleteProductModel(int productId){
        SearchParameters sp = new SearchParameters();
        Map<String,Object> par = new HashMap<String,Object>();
        par.put("productId", productId);
        sp.setNamedQuery("productModel.deleteProducts");
        sp.setNamedQueryParameters(par);
        TdProductModel pm = new TdProductModel();
        List<TdProductModel> deleteList =  tdProductModelRepository.find(pm,sp);
        for(TdProductModel productModel:deleteList)
           tdProductModelRepository.delete(productModel);
        return true;
    }
}