package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdRequestCarPart;
import com.app.mpadmin.repository.TdRequestCarPartRepository;
import com.app.mpadmin.web.converter.domain.TdRequestCarPartConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdRequestCarPartController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdRequestCarPartRepository tdRequestCarPartRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdRequestCarPartConverter tdRequestCarPartConverter;

    public TdRequestCarPart newIfNull(TdRequestCarPart tdRequestCarPart) {
        if (tdRequestCarPart != null) {
            return tdRequestCarPart;
        }

        return tdRequestCarPartRepository.getNewWithDefaults();
    }

    public boolean save(TdRequestCarPart tdRequestCarPart) {
        tdRequestCarPartRepository.save(tdRequestCarPart);
        messageUtil.info("status_saved_ok", tdRequestCarPartConverter.print(tdRequestCarPart));
        return true;
    }

    public boolean saveAndClose(TdRequestCarPart tdRequestCarPart) {
        tdRequestCarPartRepository.save(tdRequestCarPart);
        messageUtil.infoDelayed("status_saved_ok", tdRequestCarPartConverter.print(tdRequestCarPart));
        forceClose();
        return true;
    }

    public boolean delete(TdRequestCarPart tdRequestCarPart) {
        String infoArg = tdRequestCarPartConverter.print(tdRequestCarPart);
        tdRequestCarPartRepository.delete(tdRequestCarPart);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }

    public boolean updateStatus(TdRequestCarPart[] arr){
        for(TdRequestCarPart tdRequestCarPart:arr){
            switch (tdRequestCarPart.getRequestStatus()){
                case 0: tdRequestCarPart.setRequestStatus(1);break;
                case 1: tdRequestCarPart.setRequestStatus(0);break;
                default: tdRequestCarPart.setRequestStatus(1);break;
            }
            tdRequestCarPartRepository.save(tdRequestCarPart);
        }
        messageUtil.info("status_saved_ok",arr.length+" request status updated");
        return true;
    }

    public boolean deleteAll(TdRequestCarPart[] arr){
        for(TdRequestCarPart tdRequestCarPart:arr){
            tdRequestCarPartRepository.delete(tdRequestCarPart);
        }
        messageUtil.info("status_deleted_ok",arr.length+" request status deleted");
        return true;
    }
}