package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdRole;
import com.app.mpadmin.repository.TdRoleRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdRoleLazyDataModel extends GenericLazyDataModel<TdRole> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdRoleRepository tdRoleRepository;
    @Inject
    transient private TdRoleSearchForm tdRoleSearchForm;

List<TdRole> datasource;

    @Override
    public TdRole getRowData(String rowKey) {
        for(TdRole tdRole : datasource) {
            if(tdRole.getRoleId().equals(rowKey))
                return tdRole;
        }

        return null;
    }

    @Override
    public Object getRowKey(TdRole tdRole)
    {
         return tdRole.getRoleId();
    }


    /**
     * Prepare the search parameters and call the tdRoleRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdRole> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdRoleSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdRoleSearchForm.getUpdationDateRange());
        sp.addRange(tdRoleSearchForm.getIsActiveRange());
        sp.addRange(tdRoleSearchForm.getRoleIdRange());
        TdRole tdRole = tdRoleSearchForm.getTdRole();
        setRowCount(tdRoleRepository.findCount(tdRole, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data
        datasource = tdRoleRepository.find(tdRole, sp);
        return datasource;
    }
}