package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdShipmentVendor;
import com.app.mpadmin.repository.TdShipmentVendorRepository;
import com.app.mpadmin.web.converter.domain.TdShipmentVendorConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdShipmentVendorController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdShipmentVendorRepository tdShipmentVendorRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdShipmentVendorConverter tdShipmentVendorConverter;

    public TdShipmentVendor newIfNull(TdShipmentVendor tdShipmentVendor) {
        if (tdShipmentVendor != null) {
            return tdShipmentVendor;
        }

        return tdShipmentVendorRepository.getNewWithDefaults();
    }

    public boolean save(TdShipmentVendor tdShipmentVendor) {
        tdShipmentVendorRepository.save(tdShipmentVendor);
        messageUtil.info("status_saved_ok", tdShipmentVendorConverter.print(tdShipmentVendor));
        return true;
    }

    public boolean saveAndClose(TdShipmentVendor tdShipmentVendor) {
        tdShipmentVendorRepository.save(tdShipmentVendor);
        messageUtil.infoDelayed("status_saved_ok", tdShipmentVendorConverter.print(tdShipmentVendor));
        forceClose();
        return true;
    }

    public boolean delete(TdShipmentVendor tdShipmentVendor) {
        String infoArg = tdShipmentVendorConverter.print(tdShipmentVendor);
        tdShipmentVendorRepository.delete(tdShipmentVendor);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}