package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdState;
import com.app.mpadmin.repository.TdStateRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdStateLazyDataModel extends GenericLazyDataModel<TdState> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdStateRepository tdStateRepository;
    @Inject
    transient private TdStateSearchForm tdStateSearchForm;

    List<TdState> datasource;

    @Override
    public TdState getRowData(String rowKey) {
        for(TdState tdState : datasource) {
            if(tdState.getStateId().equals(rowKey))
                return tdState;
        }

        return null;
    }

    @Override
    public Object getRowKey(TdState tdState)
    {
         return tdState.getStateId();
    }


    /**
     * Prepare the search parameters and call the tdStateRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdState> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdStateSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdStateSearchForm.getImportanceRange());
        sp.addRange(tdStateSearchForm.getUpdationDateRange());

        // entity selectors
        sp.clearEntitySelectors();
        sp.addEntitySelector(tdStateSearchForm.getCountrySelector());

        TdState tdState = tdStateSearchForm.getTdState();
        setRowCount(tdStateRepository.findCount(tdState, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data
        datasource = tdStateRepository.find(tdState, sp);
        return datasource;
    }
}