package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdTag;
import com.app.mpadmin.repository.TdTagRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdTagLazyDataModel extends GenericLazyDataModel<TdTag> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdTagRepository tdTagRepository;
    @Inject
    transient private TdTagSearchForm tdTagSearchForm;

    /**
     * Prepare the search parameters and call the tdTagRepository finder.
     * Automatically called by PrimeFaces component.
     */
    List<TdTag> datasource;

    @Override
    public TdTag getRowData(String rowKey) {
        for(TdTag tdTag : datasource) {
            if(tdTag.getTagId().equals(rowKey))
                return tdTag;
        }

        return null;
    }

    @Override
    public Object getRowKey(TdTag tdTag)
    {
         return tdTag.getTagId();
    }

    @Override
    public List<TdTag> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdTagSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdTagSearchForm.getTagPriorityRange());
        sp.addRange(tdTagSearchForm.getTagTypeRange());
        sp.addRange(tdTagSearchForm.getUpdationDateRange());
        sp.addRange(tdTagSearchForm.getIsActiveRange());
        sp.addRange(tdTagSearchForm.getTagStatusRange());

        TdTag tdTag = tdTagSearchForm.getTdTag();
        setRowCount(tdTagRepository.findCount(tdTag, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data
        datasource = tdTagRepository.find(tdTag, sp);
        return  datasource;
    }
}