package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdUserActivityEvent;
import com.app.mpadmin.repository.TdUserActivityEventRepository;
import com.app.mpadmin.web.converter.domain.TdUserActivityEventConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdUserActivityEventController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdUserActivityEventRepository tdUserActivityEventRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdUserActivityEventConverter tdUserActivityEventConverter;

    public TdUserActivityEvent newIfNull(TdUserActivityEvent tdUserActivityEvent) {
        if (tdUserActivityEvent != null) {
            return tdUserActivityEvent;
        }

        return tdUserActivityEventRepository.getNewWithDefaults();
    }

    public boolean save(TdUserActivityEvent tdUserActivityEvent) {
        tdUserActivityEventRepository.save(tdUserActivityEvent);
        messageUtil.info("status_saved_ok", tdUserActivityEventConverter.print(tdUserActivityEvent));
        return true;
    }

    public boolean saveAndClose(TdUserActivityEvent tdUserActivityEvent) {
        tdUserActivityEventRepository.save(tdUserActivityEvent);
        messageUtil.infoDelayed("status_saved_ok", tdUserActivityEventConverter.print(tdUserActivityEvent));
        forceClose();
        return true;
    }

    public boolean delete(TdUserActivityEvent tdUserActivityEvent) {
        String infoArg = tdUserActivityEventConverter.print(tdUserActivityEvent);
        tdUserActivityEventRepository.delete(tdUserActivityEvent);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}