package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdUserAddress;
import com.app.mpadmin.repository.TdUserAddressRepository;
import com.app.mpadmin.web.converter.domain.TdUserAddressConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdUserAddressController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdUserAddressRepository tdUserAddressRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdUserAddressConverter tdUserAddressConverter;

    public TdUserAddress newIfNull(TdUserAddress tdUserAddress) {
        if (tdUserAddress != null) {
            return tdUserAddress;
        }

        return tdUserAddressRepository.getNewWithDefaults();
    }

    public boolean save(TdUserAddress tdUserAddress) {
        tdUserAddressRepository.save(tdUserAddress);
        messageUtil.info("status_saved_ok", tdUserAddressConverter.print(tdUserAddress));
        return true;
    }

    public boolean saveAndClose(TdUserAddress tdUserAddress) {
        tdUserAddressRepository.save(tdUserAddress);
        messageUtil.infoDelayed("status_saved_ok", tdUserAddressConverter.print(tdUserAddress));
        forceClose();
        return true;
    }

    public boolean delete(TdUserAddress tdUserAddress) {
        String infoArg = tdUserAddressConverter.print(tdUserAddress);
        tdUserAddressRepository.delete(tdUserAddress);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}