package com.app.mpadmin.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.app.mpadmin.dao.support.SearchParameters;
import com.app.mpadmin.domain.TdUserAddress;
import com.app.mpadmin.repository.TdUserAddressRepository;
import com.app.mpadmin.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class TdUserAddressLazyDataModel extends GenericLazyDataModel<TdUserAddress> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private TdUserAddressRepository tdUserAddressRepository;
    @Inject
    transient private TdUserAddressSearchForm tdUserAddressSearchForm;

    /**
     * Prepare the search parameters and call the tdUserAddressRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<TdUserAddress> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = tdUserAddressSearchForm.getSearchParameters();

        // ranges
        sp.clearRanges();
        sp.addRange(tdUserAddressSearchForm.getZipCodeRange());
        sp.addRange(tdUserAddressSearchForm.getUserDetailsIdRange());
        sp.addRange(tdUserAddressSearchForm.getUpdationDateRange());
        sp.addRange(tdUserAddressSearchForm.getIsActiveRange());

        // entity selectors
        sp.clearEntitySelectors();
        sp.addEntitySelector(tdUserAddressSearchForm.getCountrySelector());
        sp.addEntitySelector(tdUserAddressSearchForm.getStateSelector());
        sp.addEntitySelector(tdUserAddressSearchForm.getCitySelector());
        sp.addEntitySelector(tdUserAddressSearchForm.getMetroSelector());

        TdUserAddress tdUserAddress = tdUserAddressSearchForm.getTdUserAddress();
        setRowCount(tdUserAddressRepository.findCount(tdUserAddress, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data

        return tdUserAddressRepository.find(tdUserAddress, sp);
    }
}