package com.app.mpadmin.web.domain;

import static com.app.mpadmin.web.util.PrimeFacesUtil.forceClose;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.app.mpadmin.domain.TdUserDetail;
import com.app.mpadmin.repository.TdUserDetailRepository;
import com.app.mpadmin.web.converter.domain.TdUserDetailConverter;
import com.app.mpadmin.web.util.MessageUtil;

/**
 * Thin controller layer allowing you to do business validation and other conditional 
 * checks that are easier to implement in Java than in webflow's xml syntax.
 */
@Named
@Singleton
public class TdUserDetailController {
    private static final long serialVersionUID = 1L;

    @Inject
    private TdUserDetailRepository tdUserDetailRepository;

    @Inject
    private MessageUtil messageUtil;

    @Inject
    private TdUserDetailConverter tdUserDetailConverter;

    public TdUserDetail newIfNull(TdUserDetail tdUserDetail) {
        if (tdUserDetail != null) {
            return tdUserDetail;
        }

        return tdUserDetailRepository.getNewWithDefaults();
    }

    public boolean save(TdUserDetail tdUserDetail) {
        tdUserDetailRepository.save(tdUserDetail);
        messageUtil.info("status_saved_ok", tdUserDetailConverter.print(tdUserDetail));
        return true;
    }

    public boolean saveAndClose(TdUserDetail tdUserDetail) {
        tdUserDetailRepository.save(tdUserDetail);
        messageUtil.infoDelayed("status_saved_ok", tdUserDetailConverter.print(tdUserDetail));
        forceClose();
        return true;
    }

    public boolean delete(TdUserDetail tdUserDetail) {
        String infoArg = tdUserDetailConverter.print(tdUserDetail);
        tdUserDetailRepository.delete(tdUserDetail);
        messageUtil.info("status_deleted_ok", infoArg);
        return true;
    }
}